import React from "react";
import { Image } from "antd";
import { CaretRightOutlined } from "@ant-design/icons";

import { numberToCapitalString } from "@/utils";
import "./index.scss";

interface propsType {
    title: string;
    image: string;
    count?: string | number;
    hideCount?: boolean;
    onClick?: Function;
}

const Card = (props: propsType) => {
    const fallback: string =
        "data:image/png;base64,";
    return (
        <div
            onClick={() => {
                props.onClick && props.onClick();
            }}
        >
            <div className="card-image">
                {!props.hideCount ? (
                    <span className="play-count">
                        <CaretRightOutlined
                            style={{
                                color: "#fff",
                                fontSize: 14,
                                marginRight: 2,
                            }}
                        />
                        {numberToCapitalString(props.count as number)}
                    </span>
                ) : null}
                <Image
                    width={130}
                    height={130}
                    src={props.image}
                    fallback={fallback}
                    preview={false}
                    alt={props.title}
                />
            </div>
            <div className="card-title" title={props.title}>
                {props.title}
            </div>
        </div>
    );
};

export default Card;
